// src/pages/Faq.js
import React, { useState } from "react";
import { Container, Row, Col, Accordion } from "react-bootstrap";

const Faq = () => {
  return (
    <Container>
      <Row>
        <Col>
          <h2 className="text-center my-4">FAQ - Centre d'Aide</h2>
          <Accordion defaultActiveKey="0">
            <Accordion.Item eventKey="0">
              <Accordion.Header>Comment créer un compte ?</Accordion.Header>
              <Accordion.Body>
                Pour créer un compte, cliquez sur "S'inscrire" sur la page d'accueil, puis remplissez les informations nécessaires.
              </Accordion.Body>
            </Accordion.Item>
            <Accordion.Item eventKey="1">
              <Accordion.Header>Comment se connecter ?</Accordion.Header>
              <Accordion.Body>
                Entrez votre email et mot de passe dans le formulaire de connexion pour accéder à votre compte.
              </Accordion.Body>
            </Accordion.Item>
            <Accordion.Item eventKey="2">
              <Accordion.Header>Comment accéder aux recommandations ?</Accordion.Header>
              <Accordion.Body>
                Une fois connecté, cliquez sur "Découvrez nos recommandations" pour voir les suggestions personnalisées.
              </Accordion.Body>
            </Accordion.Item>
            {/* Ajouter plus de questions si nécessaire */}
          </Accordion>
        </Col>
      </Row>
    </Container>
  );
};

export default Faq;
