function Recommandations() {
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [priceFilter, setPriceFilter] = useState(0);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentLieu, setCurrentLieu] = useState(null);

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentLieu(null);
  };

  const filteredRecommendations = recommendations.filter(recommendation => (
    (categoryFilter === "All" || recommendation.categorie === categoryFilter) &&
    recommendation.prix >= priceFilter
  ));

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;
    
    for (let i = 1; i <= 5; i++) {
      if (i <= fullStars) {
        stars.push(<StarFill key={i} color="gold" />);
      } else if (i === fullStars + 1 && hasHalfStar) {
        stars.push(<StarFill key={i} color="gold" />);
      } else {
        stars.push(<Star key={i} color="gold" />);
      }
    }
    
    return stars;
  };

  // Styles
  const styles = {
    container: {
      backgroundImage: "linear-gradient(rgba(0,0,0,0.7), rgba(0,0,0,0.7)), url('./assets/images/marrakech.jpg')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      minHeight: "100vh",
      color: "white",
      padding: "40px 0",
    },
    title: {
      fontSize: "2.5rem",
      fontWeight: "bold",
      marginBottom: "30px",
      color: "white",
      textShadow: "2px 2px 4px rgba(0,0,0,0.5)",
    },
    card: {
      borderRadius: "10px",
      overflow: "hidden",
      boxShadow: "0 4px 20px rgba(0,0,0,0.2)",
      transition: "transform 0.3s, box-shadow 0.3s",
      height: "100%",
      backgroundColor: "rgba(255,255,255,0.9)",
      color: "#333",
    },
    cardHeader: {
      position: "relative",
    },
    cardImg: {
      height: "200px",
      objectFit: "cover",
    },
    favoriteBtn: {
      position: "absolute",
      top: "10px",
      right: "10px",
      background: "rgba(0,0,0,0.5)",
      border: "none",
      borderRadius: "50%",
      width: "40px",
      height: "40px",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      cursor: "pointer",
    },
    detailsBtn: {
      width: "100%",
      fontWeight: "bold",
    },
    dropdown: {
      width: "100%",
      textAlign: "left",
    },
  };

  const modalStyles = {
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      transform: "translate(-50%, -50%)",
      maxWidth: "800px",
      width: "90%",
      padding: "0",
      border: "none",
      borderRadius: "10px",
      overflow: "hidden",
    },
    overlay: {
      backgroundColor: "rgba(0, 0, 0, 0.75)",
    },
  };

  return (
    <div style={styles.container}>
      <Container className="my-5">
        <h1 className="text-center mb-4" style={styles.title}>Nos Recommandations</h1>
        
        <Row className="mb-4 justify-content-center">
          <Col md={4} className="mb-2">
            <Dropdown onSelect={(e) => setCategoryFilter(e)}>
              <Dropdown.Toggle variant="dark" id="categoryDropdown">
                {categoryFilter}
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item eventKey="All">Tout</Dropdown.Item>
                <Dropdown.Item eventKey="Restaurant">Restaurant</Dropdown.Item>
                <Dropdown.Item eventKey="Hôtel">Hôtel</Dropdown.Item>
                <Dropdown.Item eventKey="Monument">Monument</Dropdown.Item>
                <Dropdown.Item eventKey="Activité">Activité</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Col>
          <Col md={4} className="mb-2">
            <Dropdown onSelect={(e) => setPriceFilter(parseInt(e))}>
              <Dropdown.Toggle variant="dark" id="priceDropdown">
                Prix minimum: {priceFilter} MAD
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item eventKey="0">0</Dropdown.Item>
                <Dropdown.Item eventKey="100">100</Dropdown.Item>
                <Dropdown.Item eventKey="200">200</Dropdown.Item>
                <Dropdown.Item eventKey="300">300</Dropdown.Item>
                <Dropdown.Item eventKey="400">400</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Col>
        </Row>
        
        <Row>
          {filteredRecommendations.map((rec) => (
            <Col key={rec.id} md={4} className="mb-4">
              <div className="card" style={styles.card}>
                <div style={styles.cardHeader}>
                  <img src={rec.image} alt={rec.nom} style={styles.cardImg} />
                  <button 
                    style={styles.favoriteBtn} 
                    onClick={() => alert("Ajouté aux favoris")}
                  >
                    <StarFill color="yellow" />
                  </button>
                </div>
                <div className="card-body">
                  <h5 className="card-title">{rec.nom}</h5>
                  <p className="card-text">{rec.description}</p>
                  <div>{renderStars(rec.rating)}</div>
                  <Button 
                    variant="primary" 
                    style={styles.detailsBtn}
                    onClick={() => {
                      setCurrentLieu(rec);
                      setModalIsOpen(true);
                    }}
                  >
                    Détails
                  </Button>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Container>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={modalStyles}
      >
        {currentLieu && (
          <div>
            <img src={currentLieu.image} alt={currentLieu.nom} style={{ width: "100%" }} />
            <div className="p-4">
              <h2>{currentLieu.nom}</h2>
              <p>{currentLieu.description}</p>
              <div>{renderStars(currentLieu.rating)}</div>
              <p><strong>Adresse:</strong> {currentLieu.adresse}</p>
              <p><strong>Heures:</strong> {currentLieu.heures}</p>
              <p><strong>Prix:</strong> {currentLieu.prix} MAD</p>
              <Button variant="secondary" onClick={closeModal}>Fermer</Button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
}

export default Recommandations;
