import React, { useState } from 'react';
import axios from 'axios';
import { Card, Button, Modal } from 'react-bootstrap';

const RecommendationCard = () => {
    const [recommendation, setRecommendation] = useState(null);
    const [imageUrl, setImageUrl] = useState(null);
    const [modalShow, setModalShow] = useState(false);

    const fetchRecommendation = async () => {
        try {
            // 1. Récupérer la recommandation
            const response = await axios.post('http://localhost:8000/api/recommendations/create-recommendation/', {
                lieu: 'Marrakech',
                budget: 'moyen',
                activite: 'détente',
                climat: 'modéré',
                transport: 'à pied',
            });

            setRecommendation(response.data.title + " - " + response.data.description);

            // 2. Récupérer l'image
            const imageResponse = await axios.get(`http://localhost:8000/api/recommendations/get-image/Marrakech/`);
            setImageUrl(imageResponse.data.image_url);

        } catch (error) {
            console.error("Erreur lors de la récupération :", error);
        }
    };

    return (
        <div className="container mt-4">
            <h1 className="mb-4 text-center">Recommandations</h1>

            {/* Afficher la recommandation et l'image */}
            {recommendation && imageUrl && (
                <Card className="mb-4">
                    <Card.Img variant="top" src={imageUrl} style={{ height: '200px', objectFit: 'cover' }} />
                    <Card.Body>
                        <Card.Title>{recommendation}</Card.Title>
                        <Button variant="primary" onClick={() => setModalShow(true)}>
                            Voir Détails
                        </Button>
                    </Card.Body>
                </Card>
            )}

          
            {/* Modal pour afficher les détails du lieu */}
            <Modal show={modalShow} onHide={() => setModalShow(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{recommendation}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <img src={imageUrl} alt="Recommandation" style={{ width: '100%', marginBottom: '15px' }} />
                    <p>Description détaillée de la recommandation.</p>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default RecommendationCard;
