import React, { useState } from "react";
import Modal from "react-modal";
import { Container, Row, Col, Button, Dropdown, Card } from "react-bootstrap";
import { StarFill, Star } from "react-bootstrap-icons";

Modal.setAppElement("#root");

const recommendations = [
  { 
    id: 1, 
    nom: "Jardin Majorelle", 
    description: "Un magnifique jardin botanique conçu par le peintre Jacques Majorelle...", 
    image: "/images/majorel.jpg",
    categorie: "Activité", 
    prix: 150,
    rating: 4.5,
    heures: "8h - 17h30",
    adresse: "Rue Yves Saint Laurent, Marrakech"
  },
  { 
    id: 2, 
    nom: "Place Jemaa el-Fna", 
    description: "Le cœur vibrant de Marrakech avec ses marchands, musiciens...", 
    image: "/images/jamaa.jpg", 
    categorie: "Activité", 
    prix: 0,
    rating: 4.7,
    heures: "Toute la journée",
    adresse: "Centre-ville, Marrakech"
  },
  // Ajoute d'autres recommandations ici...
];

function Recommandations() {
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [priceFilter, setPriceFilter] = useState(0);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentLieu, setCurrentLieu] = useState(null);

  const openModal = (lieu) => {
    setCurrentLieu(lieu);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentLieu(null);
  };

  const filteredRecommendations = recommendations.filter(recommendation => (
    (categoryFilter === "All" || recommendation.categorie === categoryFilter) &&
    recommendation.prix >= priceFilter
  ));

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;

    for (let i = 1; i <= 5; i++) {
      if (i <= fullStars) {
        stars.push(<StarFill key={i} color="gold" />);
      } else if (i === fullStars + 1 && hasHalfStar) {
        stars.push(<StarFill key={i} color="gold" />);
      } else {
        stars.push(<Star key={i} color="gold" />);
      }
    }

    return stars;
  };

  return (
    <div style={{ padding: '40px', background: 'url(/images/marrakech.jpg) no-repeat center center', backgroundSize: 'cover', color: 'white' }}>
      <Container>
        <h1 className="text-center mb-4" style={{ fontSize: '2.5rem' }}>Nos Recommandations</h1>

        <Row className="mb-4 justify-content-center">
          <Col md={4} className="mb-2">
            <Dropdown onSelect={(e) => setCategoryFilter(e)}>
              <Dropdown.Toggle variant="dark" id="category-dropdown">
                {categoryFilter === "All" ? "Toutes catégories" : categoryFilter}
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item eventKey="All">Toutes catégories</Dropdown.Item>
                <Dropdown.Item eventKey="Activité">Activités</Dropdown.Item>
                <Dropdown.Item eventKey="Hôtel">Hôtels</Dropdown.Item>
                <Dropdown.Item eventKey="Restaurant">Restaurants</Dropdown.Item>
                <Dropdown.Item eventKey="Monument">Monuments</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Col>
          <Col md={4} className="mb-2">
            <Dropdown onSelect={(e) => setPriceFilter(Number(e))}>
              <Dropdown.Toggle variant="dark" id="price-dropdown">
                {priceFilter === 0 ? "Tous les prix" : `Prix > ${priceFilter} MAD`}
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item eventKey="0">Tous les prix</Dropdown.Item>
                <Dropdown.Item eventKey="100">Prix > 100 MAD</Dropdown.Item>
                <Dropdown.Item eventKey="200">Prix > 200 MAD</Dropdown.Item>
                <Dropdown.Item eventKey="300">Prix > 300 MAD</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Col>
        </Row>

        <Row>
          {filteredRecommendations.map((lieu) => (
            <Col key={lieu.id} md={4} className="mb-4">
              <Card style={{ height: '100%' }}>
                <Card.Img variant="top" src={lieu.image} alt={lieu.nom} style={{ height: '200px', objectFit: 'cover' }} />
                <Card.Body>
                  <Card.Title>{lieu.nom}</Card.Title>
                  <Card.Text>{lieu.description}</Card.Text>
                  <div>
                    <strong>Catégorie:</strong> {lieu.categorie}
                    <br />
                    <strong>Prix:</strong> {lieu.prix} MAD
                    <br />
                    <strong>Adresse:</strong> {lieu.adresse}
                    <br />
                    <div>
                      <strong>Note:</strong> {renderStars(lieu.rating)} ({lieu.rating}/5)
                    </div>
                  </div>
                  <Button variant="primary" onClick={() => openModal(lieu)}>Voir Détails</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
        
        <Modal 
          isOpen={modalIsOpen} 
          onRequestClose={closeModal} 
          style={{ content: { padding: '20px', maxWidth: '800px', margin: 'auto' } }}
        >
          {currentLieu && (
            <>
              <h2>{currentLieu.nom}</h2>
              <img src={currentLieu.image} alt={currentLieu.nom} style={{ width: '100%', height: '300px', objectFit: 'cover' }} />
              <p>{currentLieu.description}</p>
              <Button variant="secondary" onClick={closeModal}>Fermer</Button>
            </>
          )}
        </Modal>
      </Container>
    </div>
  );
}

export default Recommandations;
