import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

const Faq = () => {
  const [reponses, setReponses] = useState({ lieu: "", budget: "", activites: "", climat: "", transport: "" });
  const [recommandation, setRecommandation] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReponses((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let recommandations = [];
    if (reponses.lieu === "jardinMajorelle" && reponses.budget === "gratuit") recommandations.push("Visitez le Jardin Majorelle.");
    if (reponses.activites === "culturel") recommandations.push("Essayez des activités culturelles à la Médina.");
    setRecommandation(recommandations.length ? recommandations.join(" ") : "Explorez plus d'options !");
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">FAQ</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Lieu :</label>
          <select name="lieu" className="form-select" value={reponses.lieu} onChange={handleChange}>
            <option value="">-- Choisir --</option>
            <option value="jardinMajorelle">Jardin Majorelle</option>
            <option value="palaisBahiya">Palais Bahia</option>
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Budget :</label>
          <select name="budget" className="form-select" value={reponses.budget} onChange={handleChange}>
            <option value="">-- Choisir --</option>
            <option value="gratuit">Gratuit</option>
            <option value="payant">Payant</option>
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Activité :</label>
          <select name="activites" className="form-select" value={reponses.activites} onChange={handleChange}>
            <option value="">-- Choisir --</option>
            <option value="culturel">Culturel</option>
            <option value="shopping">Shopping</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-100">Voir la recommandation</button>
      </form>

      {recommandation && <div className="mt-4 alert alert-info">{recommandation}</div>}
    </div>
  );
};

export default Faq;
