import React, { useEffect, useState } from 'react';
import { NavLink, useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token);
  }, [location]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/");
  };

  // üî• Appel API m√©t√©o pour Marrakech
  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const apiKey = "70b656eeb86a87b0a345f45a938b44f4"; // üëà Colle ta cl√© API ici
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=Marrakech,MA&appid=${apiKey}&units=metric&lang=fr`
        );
        const data = await response.json();
        setWeather({
          temp: Math.round(data.main.temp),
          icon: data.weather[0].icon,
          description: data.weather[0].description,
        });
      } catch (error) {
        console.error("Erreur lors de la r√©cup√©ration m√©t√©o :", error);
      }
    };

    fetchWeather();
  }, []);

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
      <div className="container-fluid">
        <NavLink className="navbar-brand" to="/">Guide Marrakech</NavLink>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <NavLink className="nav-link" to="/">Accueil</NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/recommandations">Recommandations</NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/carte">Carte</NavLink>
            </li>
            <li className="nav-item">
              <NavLink className="nav-link" to="/chatbot">Chatbot</NavLink>
            </li>

            {/* ‚òÄÔ∏è Affichage m√©t√©o Marrakech */}
            {weather && (
              <li className="nav-item d-flex align-items-center text-white mx-3">
                <img
                  src={`http://openweathermap.org/img/wn/${weather.icon}.png`}
                  alt={weather.description}
                  style={{ width: "30px", height: "30px", marginRight: "5px" }}
                />
                <span>
                  Marrakech {weather.temp}¬∞C, {weather.description}
                </span>
              </li>
            )}

            {isLoggedIn && (
              <li className="nav-item">
                <button className="btn btn-outline-light ms-3" onClick={handleLogout}>
                  Se d√©connecter
                </button>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
