import React, { useState, useEffect } from "react";
import { Container, Row, Col, Button, Modal, Form, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Accueil() {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [isRegistering, setIsRegistering] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [weather, setWeather] = useState(null); // New state for weather data
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch weather data from backend
  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/recommendations/weather/Marrakech/"); // Adjust to your actual API endpoint
        if (!response.ok) {
          throw new Error("Erreur lors de la récupération des données météo");
        }
        const data = await response.json();
        setWeather(data);
        setIsLoading(false);
      } catch (error) {
        setError(error.message);
        setIsLoading(false);
      }
    };

    fetchWeather();
  }, []);

  const goToRecommendations = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setShowModal(true);
    } else {
      navigate("/recommandations");
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    if (email && password && username) {
      try {
        const response = await axios.post("http://127.0.0.1:8000/api/recommendations/register/", {
          username,
          email,
          password,
        });
        localStorage.setItem("token", response.data.access);
        setIsRegistering(false);
      } catch (err) {
        alert("Erreur lors de l'inscription, veuillez réessayer.");
      }
    } else {
      alert("Veuillez remplir tous les champs.");
    }
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    if (email && password) {
      try {
        const response = await axios.post("http://127.0.0.1:8000/api/recommendations/login/", {
          username: email,
          password,
        });
        localStorage.setItem("token", response.data.access);
        setShowModal(false);
        navigate("/recommandations");
      } catch (err) {
        alert("Erreur de connexion, veuillez vérifier vos identifiants.");
      }
    } else {
      alert("Veuillez remplir tous les champs.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const isLoggedIn = localStorage.getItem("token");

  return (
    <div style={styles.container}>
      <Container className="text-center text-white d-flex align-items-center justify-content-center" style={styles.overlay}>
        <Row>
          <Col md={12}>
            <h1 className="display-4">Bienvenue à Marrakech !</h1>
            <p className="lead">Découvrez les meilleures activités, hôtels et restaurants adaptés à votre budget.</p>

            <Button variant="danger" onClick={goToRecommendations} size="lg" className="mb-2">
              Découvrez nos recommandations
            </Button>

            {/* Bouton vers la page d’aide */}
            <div>
              <Button variant="outline-light" onClick={() => navigate("/faq")} size="sm">
                Besoin d’aide ? FAQ
              </Button>
            </div>

            {/* Affichage de la météo */}
            <div className="mt-4">
              {isLoading ? (
                <p>Chargement de la météo...</p>
              ) : error ? (
                <p>Erreur météo: {error}</p>
              ) : weather ? (
                <Card className="bg-dark text-white" style={{ maxWidth: '300px', margin: '0 auto' }}>
                  <Card.Body>
                    <Card.Title>Météo actuelle à Marrakech</Card.Title>
                    <div className="d-flex align-items-center">
                      <img
                        src={`http://openweathermap.org/img/wn/${weather.icon}.png`}
                        alt={weather.description}
                        style={{ width: "50px", height: "50px", marginRight: "10px" }}
                      />
                      <div>
                        <h6>{weather.temp}°C</h6>
                        <small>{weather.description}</small>
                      </div>
                    </div>
                  </Card.Body>
                </Card>
              ) : (
                <p>Aucune donnée météo disponible.</p>
              )}
            </div>

            {isLoggedIn && (
              <Button variant="secondary" onClick={handleLogout} className="mt-3">
                Déconnexion
              </Button>
            )}
          </Col>
        </Row>
      </Container>

      {/* Modale pour inscription / connexion */}
      <Modal show={showModal} onHide={() => setShowModal(false)} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>{isRegistering ? "Inscription" : "Connexion"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={isRegistering ? handleRegisterSubmit : handleLoginSubmit}>
            {isRegistering && (
              <Form.Group className="mb-3">
                <Form.Label>Nom d'utilisateur</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Entrez votre nom d'utilisateur"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>
            )}

            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Entrez votre email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Mot de passe</Form.Label>
              <Form.Control
                type="password"
                placeholder="Entrez votre mot de passe"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit" className="w-100">
              {isRegistering ? "S'inscrire" : "Se connecter"}
            </Button>
          </Form>

          <div className="mt-3 text-center">
            {isRegistering ? (
              <p>
                Vous avez déjà un compte ?{" "}
                <Button variant="link" onClick={() => setIsRegistering(false)}>
                  Se connecter
                </Button>
              </p>
            ) : (
              <p>
                Vous n'êtes pas encore inscrit ?{" "}
                <Button variant="link" onClick={() => setIsRegistering(true)}>
                  S'inscrire
                </Button>
              </p>
            )}
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
}

const styles = {
  container: {
    backgroundImage: "url('/images/HAJAR.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    height: "100vh",
    width: "100vw",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  overlay: {
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    padding: "50px",
    minHeight: "100vh",
    width: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
};

export default Accueil;
