import React, { useState } from "react";
import Modal from "react-modal";
import { Container, Row, Col, Button, Dropdown } from "react-bootstrap";
import { StarFill, Star } from "react-bootstrap-icons";

// Importer les images
import majorel from '/images/majorel.jpg';
import jamaa from '/images/jamaa.jpg';
import bahiaa from '/images/bahiaa.jpg';
import bahia from '/images/bahia.jpg';
import tobsil from '/images/tobsil.jpg';
import museePalmeraie from '/images/musee_palmeraie.jpg';
import riadKheirredine from '/images/riad_kheirredine.jpg';
import jardinSecret from '/images/jardin_secret.jpg';
import palaisElBadi from '/images/palais_el_badi.jpg';
import leFoundouk from '/images/le_foundouk.jpg';
import soukSemmarine from '/images/souk_semmarine.jpg';
import cafeEpices from '/images/cafe_epices.jpg';
import royalMansour from '/images/royal_mansour.jpg';
import menaraGardens from '/images/menara_gardens.jpg';
import comptoirDarna from '/images/comptoir_darna.jpg';

Modal.setAppElement("#root");

const recommendations = [
  { 
    id: 1, 
    nom: "Jardin Majorelle", 
    description: "Un magnifique jardin botanique conçu par le peintre Jacques Majorelle, puis restauré par Yves Saint Laurent. Abrite une riche collection de plantes exotiques et un musée berbère.", 
    image: majorel,
    categorie: "Activité", 
    prix: 150,
    rating: 4.5,
    heures: "8h - 17h30",
    adresse: "Rue Yves Saint Laurent, Marrakech"
  },
  { 
    id: 2, 
    nom: "Place Jemaa el-Fna", 
    description: "Le cœur vibrant de Marrakech avec ses marchands, musiciens et conteurs.", 
