import React, { useState } from 'react';
import axios from 'axios';
import { Button } from "react-bootstrap";  // Assure-toi d'importer 'Button' depuis react-bootstrap

const RecommendationCard = () => {
    const [recommendation, setRecommendation] = useState(null);
    const [imageUrl, setImageUrl] = useState(null);

    const fetchRecommendation = async () => {
        try {
            // Demander la recommandation
            const response = await axios.post('http://localhost:8000/api/recommendations/generate-recommendation/', {
                lieu: 'Marrakech',
                budget: 'moyen',
                activite: 'détente',
                climat: 'modéré',
                transport: 'à pied',
            });

            setRecommendation(response.data.recommendation);

            // Demander l'image associée au lieu
            const imageResponse = await axios.get(`http://localhost:8000/api/recommendations/get-image/${'Marrakech'}/`);
            setImageUrl(imageResponse.data.image_url);
        } catch (error) {
            console.error('Erreur lors de la récupération de la recommandation:', error);
        }
    };

    return (
        <div>
            <Button onClick={fetchRecommendation}>Obtenir une recommandation</Button> {/* Le bouton qui déclenche l'action */}

            {recommendation && imageUrl && (
                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src={imageUrl} />
                    <Card.Body>
                        <Card.Title>Recommandation de voyage</Card.Title>
                        <Card.Text>{recommendation}</Card.Text>
                    </Card.Body>
                </Card>
            )}
        </div>
    );
};

export default RecommendationCard;
