import React, { useState, useEffect } from "react";
import axios from "axios";
import { Card, Button, Row, Col, Modal } from "react-bootstrap";
import { HeartFill, Heart } from "react-bootstrap-icons";

function RecommendationCard() {
  const [recommendations, setRecommendations] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [selectedLieu, setSelectedLieu] = useState(null);
  const [modalShow, setModalShow] = useState(false);

  useEffect(() => {
    fetchRecommendations();
  }, []);

  const fetchRecommendations = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/recommendations/");
      setRecommendations(response.data);
    } catch (error) {
      console.error("Erreur lors de la récupération des recommandations :", error);
    }
  };

  const toggleFavorite = (id) => {
    if (favorites.includes(id)) {
      setFavorites(favorites.filter((favId) => favId !== id));
    } else {
      setFavorites([...favorites, id]);
    }
  };

  const openModal = (lieu) => {
    setSelectedLieu(lieu);
    setModalShow(true);
  };

  const renderStars = (rating) => {
    const stars = [];
    const roundedRating = Math.round(rating);
    for (let i = 0; i < 5; i++) {
      stars.push(
        <span key={i} style={{ color: i < roundedRating ? "gold" : "lightgray" }}>★</span>
      );
    }
    return stars;
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4 text-center">Recommandations</h1>
      <Row xs={1} md={2} lg={3} className="g-4">
        {recommendations.map((lieu) => (
          <Col key={lieu.id}>
            <Card style={{ height: "100%", display: "flex", flexDirection: "column" }}>
              <div style={{ position: "relative" }}>
                <Card.Img variant="top" src={lieu.image} style={{ height: "200px", objectFit: "cover" }} />
                <Button 
                  variant="link" 
                  style={{
                    position: "absolute", 
                    top: "10px", 
                    right: "10px", 
                    backgroundColor: "rgba(255,255,255,0.7)", 
                    borderRadius: "50%", 
                    padding: "5px"
                  }}
                  onClick={() => toggleFavorite(lieu.id)}
                >
                  {favorites.includes(lieu.id) ? <HeartFill color="red" size={20} /> : <Heart color="red" size={20} />}
                </Button>
              </div>
              <Card.Body style={{ flex: "1" }}>
                <Card.Title>{lieu.nom}</Card.Title>
                <Card.Text>{lieu.description.slice(0, 80)}...</Card.Text>
                <div style={{ marginBottom: "10px" }}>
                  {renderStars(lieu.rating)}
                </div>
                <Button variant="danger" onClick={() => openModal(lieu)} style={{ width: "100%" }}>
                  Voir Détails
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Modal pour afficher les détails du lieu */}
      <Modal show={modalShow} onHide={() => setModalShow(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedLieu?.nom}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <img src={selectedLieu?.image} alt={selectedLieu?.nom} style={{ width: "100%", marginBottom: "15px" }} />
          <p>{selectedLieu?.description}</p>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default RecommendationCard;
